import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
class Square extends React.Component{
  constructor(props){
    super(props);
  }
  render(){
    return(
      <button className="square" onClick={()=>{this.props.onclick()}} >
        {this.props.value}
      </button>
    )
  }
}
class Reset extends React.Component{
  constructor(props){
    super(props);
  }
  render(){
    return(
      <button className="reset" onClick={()=>{this.props.onclick()}}>
          Reset
      </button>
    )
  }
}
class Board extends React.Component{
  constructor(props){
    super(props);
    this.state={
      squares: Array(9).fill(null),
      turn:0,
    }
  }
  handleReset(){
    this.setState({squares:Array(9).fill(null)});
  }
  handleClick(i){
    let store=this.state.turn;
    const squares = this.state.squares.slice();
    if (store%2==0){
      if ((squares[i]!=='X')&&(squares[i]!=='O')){
        squares[i]='X';
        store=store+1;
      }
    }else{
      if ((squares[i]!=='X')&&(squares[i]!='O')){
        squares[i]='O';
        store=store+1;
      }
    }
    this.setState({squares:squares, turn:store});
  }
  renderSquare(i){
    return (
      <Square value={this.state.squares[i]} onclick={()=>this.handleClick(i)}/>
    )
  }
  render(){
    return(
      <div>
        <div className="row_1">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="row_2">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="row_3">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
        <Reset onClick={()=>{this.handleReset()}}/>
      </div>
    )
  }
}
ReactDOM.render(
    <Board />,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
