import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
class Square extends React.Component{
  constructor(props){
    super(props);
  }
  render(){
    return(
      <button className="square" onClick={()=>{this.props.onclick()}} >
        {this.props.value}
      </button>
    )
  }
}
class Board extends React.Component{
  constructor(props){
    super(props);
    this.state={
      squares: Array(9).fill(null),
      turn:0,
    }
  }
  handleClick(i){
    const squares = this.state.squares.slice();
    if (turn%2==0){
      squares[i]='X';
    }else{
      squares[i]='O';
    }
    this.setState({squares:squares});
  }
  renderSquare(i){
    return (
      <Square value={this.state.squares[i]} onclick={()=>this.handleClick(i)}/>
    )
  }
  render(){
    return(
      <div>
        <div className="row_1">
          {this.renderSquare(0)}
          {this.renderSquare(1)}
          {this.renderSquare(2)}
        </div>
        <div className="row_2">
          {this.renderSquare(3)}
          {this.renderSquare(4)}
          {this.renderSquare(5)}
        </div>
        <div className="row_3">
          {this.renderSquare(6)}
          {this.renderSquare(7)}
          {this.renderSquare(8)}
        </div>
      </div>
    )
  }
}
ReactDOM.render(
    <Board />,
  document.getElementById('root')
);

