import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
class Square extends React.Component{
  constructor(props){
    super(props);
  }
  render(){
    return(
      <button className="square" onClick={()=>{this.props.onclick()}} >
        {this.props.value}
      </button>
    )
  }
}
class Reset extends React.Component{
  constructor(props){
    super(props);
  }
  render(){
    return(
      <div className="reset" >
        <button onClick={()=>{this.props.onClick()}}>
            Reset
        </button>
      </div>
    )
  }
}
class GameOver extends React.Component{
  constructor(props){
    super(props);
  }
  render(){
    return(
      <hi>Game Over</hi>
    )
  }
}
class Board extends React.Component{
  constructor(props){
    super(props);
    this.state={
      squares: Array(9).fill(null),
      turn:0,
    }
  }
  handleReset(){
    this.setState({squares:Array(9).fill(null)});
  }
  checkBoard(){
    for (let i=0;i<=6;i+=3){
      if ((this.state.squares[i]==this.state.squares[i+1])&&(this.state.squares[i+2]==this.state.squares[i+1])){
        this.handleReset();
        return;
      }
    }
    for (let i=0;i<3;i++){
      if ((this.state.squares[i]==this.state.squares[i+3])&&(this.state.squares[i+3]==this.state.squares[i+6])){
        this.handleReset();
        return;
      }
    }
    if ((this.state.squares[0]==this.state.squares[4])&&(this.state.squares[4]==this.state.squares[8])){
      this.handleReset();
      return;
    }
    if ((this.state.squares[2]==this.state.squares[4])&&(this.state.squares[4]==this.state.squares[6])){
      this.handleReset();
      return;
    }
  }
  handleClick(i){
    let store=this.state.turn;
    const squares = this.state.squares.slice();
    if (store%2==0){
      if ((squares[i]!=='X')&&(squares[i]!=='O')){
        squares[i]='X';
        store=store+1;
      }
    }else{
      if ((squares[i]!=='X')&&(squares[i]!=='O')){
        squares[i]='O';
        store=store+1;
      }
    }
    this.setState({squares:squares, turn:store});
  }
  renderSquare(i){
    return (
      <Square value={this.state.squares[i]} onclick={()=>this.handleClick(i)}/>
    )
  }
  render(){
    this.checkBoard();
      return(
        <div>
          <div className="row_1">
            {this.renderSquare(0)}
            {this.renderSquare(1)}
            {this.renderSquare(2)}
          </div>
          <div className="row_2">
            {this.renderSquare(3)}
            {this.renderSquare(4)}
            {this.renderSquare(5)}
          </div>
          <div className="row_3">
            {this.renderSquare(6)}
            {this.renderSquare(7)}
            {this.renderSquare(8)}
          </div>
          <Reset onClick={()=>{this.handleReset()}}/>
          <GameOver/>
        </div>
      )
    }
  }
ReactDOM.render(
    <Board />,
  document.getElementById('root')
);

